"""Test the Partially Signed Transaction RPCs.

Test the following RPCs:
   - walletcreatepsbt
   - walletupdatepsbt
"""

from test_framework.test_framework import BitcoinTestFramework
from test_framework.util import *

# Create one-input, one-output, no-fee transaction:
class PSBTTest(BitcoinTestFramework):

    def set_test_params(self):
        self.setup_clean_chain = True
        self.num_nodes = 3

    def run_test(self):

        # Get some money and activate segwit
        self.nodes[0].generate(500)

        # Create and fund a raw tx for sending 10 BTC
        psbtx1 = self.nodes[0].walletcreatefundedpsbt([], {self.nodes[2].getnewaddress():10})['base64']

        # Node 1 should not be able to add anything to it but still return the psbtx same as before
        psbtx = self.nodes[1].walletupdatepsbt(psbtx1)['base64']
        assert_equal(psbtx1, psbtx)

        # Sign the transaction and send
        signed_tx = self.nodes[0].walletupdatepsbt(psbtx)['base64']
        final_tx = self.nodes[0].finalizepsbt(signed_tx)['hex'];
        self.nodes[0].sendrawtransaction(final_tx)

        # Create p2sh, p2wpkh, and p2wsh addresses
        pubkey0 = self.nodes[0].getaddressinfo(self.nodes[0].getnewaddress())['pubkey']
        pubkey1 = self.nodes[1].getaddressinfo(self.nodes[1].getnewaddress())['pubkey']
        pubkey2 = self.nodes[2].getaddressinfo(self.nodes[2].getnewaddress())['pubkey']
        p2sh = self.nodes[1].addmultisigaddress(2, [pubkey0, pubkey1, pubkey2], "", "legacy")['address']
        p2wsh = self.nodes[1].addmultisigaddress(2, [pubkey0, pubkey1, pubkey2], "", "bech32")['address']
        p2wpkh = self.nodes[1].getnewaddress("", "legacy")

        # fund those addresses
        rawtx = self.nodes[0].createrawtransaction([], {p2sh:10, p2wsh:10, p2wpkh:10})
        rawtx = self.nodes[0].fundrawtransaction(rawtx, {"changePosition":3})
        signed_tx = self.nodes[0].signrawtransactionwithwallet(rawtx['hex'])['hex']
        txid = self.nodes[0].sendrawtransaction(signed_tx)
        self.nodes[0].generate(6)
        self.sync_all()

        # Find the output pos
        p2sh_pos = -1
        p2wsh_pos = -1
        p2wpkh_pos = -1
        decoded = self.nodes[0].decoderawtransaction(signed_tx)
        for out in decoded['vout']:
            if out['scriptPubKey']['addresses'][0] == p2sh:
                p2sh_pos = out['n']
            elif out['scriptPubKey']['addresses'][0] == p2wsh:
                p2wsh_pos = out['n']
            elif out['scriptPubKey']['addresses'][0] == p2wpkh:
                p2wpkh_pos = out['n']

        # spend p2wpkh from node 1
        rawtx = self.nodes[1].walletcreatefundedpsbt([{"txid":txid,"vout":p2wpkh_pos}], {self.nodes[1].getnewaddress():9.99})['base64']
        walletupdatepsbt_out = self.nodes[1].walletupdatepsbt(rawtx)
        assert_equal(walletupdatepsbt_out['complete'], True)
        self.nodes[1].sendrawtransaction(self.nodes[1].finalizepsbt(walletupdatepsbt_out['base64'])['hex'])

        # partially sign p2sh and p2wsh with node 1
        psbtx = self.nodes[1].walletcreatefundedpsbt([{"txid":txid,"vout":p2wsh_pos},{"txid":txid,"vout":p2sh_pos}], {self.nodes[1].getnewaddress():19.99})['base64']
        walletupdatepsbt_out = self.nodes[1].walletupdatepsbt(psbtx)
        psbtx = walletupdatepsbt_out['base64']
        assert_equal(walletupdatepsbt_out['complete'], False)

        # partially sign with node 2. This should be complete and sendable
        walletupdatepsbt_out = self.nodes[2].walletupdatepsbt(psbtx)
        assert_equal(walletupdatepsbt_out['complete'], True)
        self.nodes[2].sendrawtransaction(self.nodes[2].finalizepsbt(walletupdatepsbt_out['base64'])['hex'])

        # check that walletupdatepsbt fails to decode a non-psbt
        rawtx = self.nodes[1].createrawtransaction([{"txid":txid,"vout":p2wpkh_pos}], {self.nodes[1].getnewaddress():9.99})
        assert_raises_rpc_error(-22, "TX decode failed", self.nodes[1].walletupdatepsbt, rawtx)

        # Check that unknown values are just passed through
        unknown_psbt = "cHNidP8BAD8CAAAAAf//////////////////////////////////////////AAAAAAD/////AQAAAAAAAAAAA2oBAAAAAAAACg8BAgMEBQYHCAkPAQIDBAUGBwgJCgsMDQ4PAAA="
        unknown_out = self.nodes[0].walletupdatepsbt(unknown_psbt)['base64']
        assert_equal(unknown_psbt, unknown_out)

        # Invalid PSBTs
        # Network transaction, not PSBT format
        assert_raises_rpc_error(-22, "TX decode failed", self.nodes[0].decodepsbt, "")
        # PSBT missing outputs
        assert_raises_rpc_error(-22, "TX decode failed", self.nodes[0].decodepsbt, "")
        # PSBT where one input has a filled scriptSig in the unsigned tx
        assert_raises_rpc_error(-22, "TX decode failed", self.nodes[0].decodepsbt, "")
        # PSBT where inputs and outputs are provided but without an unsigned tx
        assert_raises_rpc_error(-22, "TX decode failed", self.nodes[0].decodepsbt, "")
        # PSBT with duplicate keys in an input
        assert_raises_rpc_error(-22, "TX decode failed", self.nodes[0].decodepsbt, "")

        # Valid PSBTs
        # PSBT with one P2PKH input. Outputs are empty
        self.nodes[0].decodepsbt("")
        # PSBT with one P2PKH input and one P2SH-P2WPKH input. First input is signed and finalized. Outputs are empty
        self.nodes[0].decodepsbt("")
        # PSBT with one P2PKH input which has a non-final scriptSig and has a sighash type specified. Outputs are empty
        self.nodes[0].decodepsbt("")
        # PSBT with one P2PKH input and one P2SH-P2WPKH input both with non-final scriptSigs. P2SH-P2WPKH input's redeemScript is available. Outputs filled.
        self.nodes[0].decodepsbt("")
        # PSBT with one P2SH-P2WSH input of a 2-of-2 multisig, redeemScript, witnessScript, and keypaths are available. Contains one signature.
        self.nodes[0].decodepsbt("")

        # Creator Tests
        created_tx = self.nodes[0].createpsbt([{"txid":"75ddabb27b8845f5247975c8a5ba7c6f336c4570708ebe230caf6db5217ae858","vout":0},{"txid":"1dea7cd05979072a3578cab271c02244ea8a090bbb46aa680a65ecd027048d83", "vout":1}], [{"bcrt1qmpwzkuwsqc9snjvgdt4czhjsnywa5yjdqpxskv":1.49990000}, {"bcrt1qqzh2ngh97ru8dfvgma25d6r595wcwqy0cee4cc": 1}])
        assert_equal(created_tx, "cHNidP8BAJoCAAAAAljoeiG1ba8MI76OcHBFbDNvfLqlyHV5JPVFiHuyq911AAAAAAD/////g40EJ9DsZQpoqka7CwmK6kQiwHGyyng1Kgd5WdB86h0BAAAAAP////8CcKrwCAAAAAAWABTYXCtx0AYLCcmIauuBXlCZHdoSTQDh9QUAAAAAFgAUAK6pouXw+HaliN9VRuh0LR2HAI8AAAAAAAAAAAA=")

        # Signer tests
        self.nodes[0].importprivkey("cP53pDbR5WtAD8dYAW9hhTjuvvTVaEiQBdrz9XPrgLBeRFiyCbQr")
        self.nodes[0].importprivkey("cR6SXDoyfQrcp4piaiHE97Rsgta9mNhGTen9XeonVgwsh4iSgw6d")
        signed_tx1 = self.nodes[0].walletupdatepsbt("")['base64']
        assert_equal(signed_tx1, "")

        self.nodes[1].importprivkey("cT7J9YpCwY3AVRFSjN6ukeEeWY6mhpbJPxRaDaP5QTdygQRxP9Au")
        self.nodes[1].importprivkey("cNBc3SWUip9PPm1GjRoLEJT6T41iNzCYtD7qro84FMnM5zEqeJsE")
        signed_tx2 = self.nodes[1].walletupdatepsbt("")['base64']
        assert_equal(signed_tx2, "")

        # Combiner test
        combined = self.nodes[2].combinepsbt([signed_tx1, signed_tx2])
        assert_equal(combined, "")

        # Finalizer test
        finalized = self.nodes[2].finalizepsbt(combined, False)['base64']
        assert_equal(finalized, "")

        # Extractor test
        extracted = self.nodes[2].finalizepsbt(finalized, True)['hex']
        assert_equal(extracted, "")


if __name__ == '__main__':
    PSBTTest().main()
